import React, { useState, useEffect} from 'react';
import '../App.css';
import { Button } from './Button';
import { Button2 } from './Button2';
import './Main.css';
import { TypeAnimation } from 'react-type-animation';       

function Main() { 
  return (
    <div className='main-container'>
        <video src="/videos/wave.mp4" autoPlay loop muted />
        <TypeAnimation className='name' sequence={["Sierra Williams", 1000]} speed={20} style={{ fontSize: '8rem', fontFamily: 'Shrikhand', textShadow: '3px 2px #f67c90', color: '#fff'}}/> 
        <p className='title'>Web Developer</p>
        <div className='main-btns'>
            <a href="/work"><Button className='btns' buttonStyle='btn--outline'
            buttonSize='btn--large'>
                View My Work
            </Button>
            </a>
            <Button2 className='btns' buttonStyle='btn--primary'
            buttonSize='btn--large'>
                Contact Me
            </Button2>
        </div>
    </div>
  )
};

export default Main;
